jQuery.fn.rlChecked=function(){jQuery("input[type=checkbox], input[type=radio]").before("<span>elements</span>");jQuery("input[type=checkbox]").parents("li").addClass("checkboxes");jQuery("input[type=checkbox]").each(function(){if(jQuery(this).is(":checked")){jQuery(this).siblings("span").addClass("checked")}});jQuery("input[type=checkbox]").change(function(){if(jQuery(this).is(":checked")){jQuery(this).siblings("span").addClass("checked")}else{jQuery(this).siblings("span").removeClass("checked")}});jQuery("input[type=radio]").parents("li").addClass("radios");jQuery("input[type=radio]").each(function(){if(jQuery(this).is(":checked")){jQuery(this).siblings("span").addClass("radio")}});jQuery("input[type=radio]").change(function(){if(jQuery(this).is(":checked")){jQuery(this).parents().find(".radio").removeClass("radio");jQuery(this).siblings("span").addClass("radio")}else{jQuery(this).click().siblings("span").addClass("radio")}})};